package com.glab_303_2_1;

public class LogicalOperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	     boolean x = true;
	       boolean y = false;
	       System.out.println("x & y : " + (x & y));
	       System.out.println("x && y : " + (x && y));
	       System.out.println("x | y : " + (x | y));
	       System.out.println("x || y: " + (x || y));
	       System.out.println("x ^ y : " + (x ^ y));
	       System.out.println("!x : " + (!x));

	}

}
